import { getDateFormatted } from "../../helpers/utils";

import save from "../../assets/images/icons/bookmark-light.svg";

const EventCardLG = ({
    author,
    content,
    description,
    source,
    title,
    urlToImage,
}) => {
  return (
    <div className="event-card-lg">
      <div className="event-card__info">
        <img className="event-card__img" src={urlToImage} alt={title} />
        <div className="event-card__img-bg"></div>
        <h5 className="event-card__title">{title}<button className="btn">Read Now</button></h5>
      </div>
    </div>
  );
};

export default EventCardLG;
