import { FreeMode } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";

import LocationBar from "./components/LocationBar/LocationBar";
import Input from "../../shared/Input/Input";
import EventCardLG from "../../shared/EventCardLG/EventCardLG";
import EventCardSM from "../../shared/EventCardSM/EventCardSM";
import Blog from "../Blog/Blog";

import search from "../../assets/images/icons/search.svg";
import eventImageOne from "../../assets/images/start-screen-img.png";
import eventImageTwo from "../../assets/images/Trump.png";
import eventImageThree from "../../assets/images/Sport.png";
import memberPlaceholderOne from "../../assets/images/Food.png";
import memberPlaceholderTwo from "../../assets/images/Medicin.png";
import memberPlaceholderThree from "../../assets/images/artificial-intelligence.png";
import { useState } from "react";
import { useEffect } from "react";

const events = [
  {
    eventID: 1,
    eventName: "Joe Biden in Press Conference USA...",
    eventDescription:
      "Lorem ipsum dolor sit amet about Cocktail party 'Goodbye Summer'",
    eventImage: eventImageOne,
    peopleJoined: [
      {
        userID: 1,
        userPicture: memberPlaceholderOne,
      },
      {
        userID: 2,
        userPicture: memberPlaceholderTwo,
      },
      {
        userID: 3,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 4,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 5,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 6,
        userPicture: memberPlaceholderThree,
      },
    ],
  },
  {
    eventID: 2,
    eventName: "Wow! USA Develops New Way of Faster and More",
    eventImage: eventImageThree,
    peopleJoined: [
      {
        userID: 1,
        userPicture: memberPlaceholderOne,
      },
      {
        userID: 2,
        userPicture: memberPlaceholderTwo,
      },
      {
        userID: 3,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 4,
        userPicture: memberPlaceholderThree,
      },
    ],
  },
  {
    eventID: 3,
    eventName: "Hot! Donald Trump Alludes to President Joe",
    eventImage: eventImageTwo,
    peopleJoined: [
      {
        userID: 1,
        userPicture: memberPlaceholderOne,
      },
      {
        userID: 2,
        userPicture: memberPlaceholderTwo,
      },
      {
        userID: 3,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 4,
        userPicture: memberPlaceholderThree,
      },
      {
        userID: 5,
        userPicture: memberPlaceholderThree,
      },
    ],
  },
  {
    eventID: 4,
    eventName: "Manchester United Adds a Famous Portugal",
    eventImage: memberPlaceholderOne,
  },
  {
    eventID: 5,
    eventName: "China Develops on Advanced Covid-19 Vaccines",
    eventImage: memberPlaceholderTwo,
  },
  {
    eventID: 6,
    eventName: "A Japanese Company Develops a New Model of",
    eventImage: memberPlaceholderThree,
  },
];

const Home = () => {
  const params = {
    slidesPerView: "auto",
    spaceBetween: 16,
    freeMode: true,
  };

  const [articles, setArticles] = useState([]);
  useEffect(() => {
    var url =
      "https://news-app-api.k8s.devebs.net/articles"
    var req = new Request(url);

    fetch(req)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data.results);
        setArticles(data.results);
      });
  }, []);
  const [modalActive, setModalActive] = useState(true)
  return (
    <div className="homepage">
      <LocationBar />
      <Input
        type="text"
        placeholder="Search for news"
        icon={search}
        iconType="button"
        mt="16"
        mb="16"
      />
      <Swiper {...params} className="events-slider" modules={[FreeMode]}>
        {!!articles &&
          articles.length > 0 &&
          articles.map((item, index) => {
            const {
             author,
             category,
             tags,
             content,
             description,
             title,
             views_count,
             image,
            } = item;
            return (
              <SwiperSlide key={index}>
                <EventCardLG
                  title={title}
                  urlToImage={urlToImage}
                />
              </SwiperSlide>
            );
          })}
      </Swiper>
      <div className="upcoming-events">
        <div className="upcoming-events__title">
          <h5>News</h5>
          <a href="/upcoming-events">See all</a>
        </div>
        {!!articles &&
          articles.length > 0 &&
          articles.map((item, index) => {
            const {
             author,
             content,
             description,
             source,
             title,
             urlToImage,
            } = item;
            return (
              <EventCardSM
              key={index}
              title={title}
              urlToImage={urlToImage}
              />
            );
          })}
      </div>
      {/* <Modal active={modalActive} setArticles={setModalActive}/> */}
    </div>
  );
};

export default Home;
