import { FreeMode } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";

import LocationBar from "./components/LocationBar/LocationBar";
import Input from "../../shared/Input/Input";
import EventCardLG from "../../shared/EventCardLG/EventCardLG";
import EventCardSM from "../../shared/EventCardSM/EventCardSM";
import Modal from "../../shared/Modal/Modal";

import search from "../../assets/images/icons/search.svg";
import { useState } from "react";
import { useEffect } from "react";

const Home = () => {
  const [articles, setArticles] = useState([]);
  const [modalActive, setModalActive] = useState(true);

  const params = {
    slidesPerView: "auto",
    spaceBetween: 16,
    freeMode: true,
  };

  useEffect(() => {
    fetch("https://news-app-api.k8s.devebs.net/articles")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setArticles(data.results);
      });
  }, []);

  return (
    <div className="homepage">
      <LocationBar />
      <Input
        type="text"
        placeholder="Search for news"
        icon={search}
        iconType="button"
        mt="16"
        mb="16"
      />
      <Swiper {...params} className="events-slider" modules={[FreeMode]}>
        {!!articles &&
          articles.length > 0 &&
          articles.map((item, index) => {
            const {
              author,
              category,
              tags,
              content,
              description,
              title,
              views_count,
              image,
            } = item;
            return (
              <SwiperSlide key={index}>
                <EventCardLG
                  author={author}
                  category={category}
                  tags={tags}
                  content={content}
                  description={description}
                  title={title}
                  views_count={views_count}
                  image={image}
                />
              </SwiperSlide>
            );
          })}
      </Swiper>
      <div className="upcoming-events">
        <div className="upcoming-events__title">
          <h5>News</h5>
          <a href="/upcoming-events">See all</a>
        </div>
        {!!articles &&
          articles.length > 0 &&
          articles.map((item, index) => {
            const {
              author,
              category,
              tags,
              content,
              description,
              title,
              views_count,
              image,
            } = item;
            return (
              <EventCardSM
                key={index}
                author={author}
                category={category}
                tags={tags}
                content={content}
                description={description}
                title={title}
                views_count={views_count}
                image={image}
              />
            );
          })}
      </div>
      <Modal setArticles={setModalActive} />
    </div>
  );
};

export default Home;
