import React from "react";

import arrowBack from "../../assets/images/icons/arrow-back.svg";

const Modal = ({ item, setModalActiveHandler }) => {
  const {
    author,
    category,
    tags,
    content,
    description,
    title,
    views_count,
    image,
  } = item;
  return (
    <div className="modal">
      <button
        onClick={() => {
          setModalActiveHandler(false);
        }}
      >
        <img src={arrowBack} alt="" />
      </button>
      <img src={image} alt="" />
      <p>{title}</p>
      <p>{content}</p>
    </div>
  );
};

export default Modal;
