import { getDateFormatted } from "../../helpers/utils";

import save from "../../assets/images/icons/bookmark.svg";
import like from "../../assets/images/icons/like.svg";
import comment from "../../assets/images/icons/comment.svg";
import chanel from "../../assets/images/icons/chanel.svg";

const EventCardSM = ({
  eventImage,
  eventTitle,
  eventDescription,
  }) => {
  return (
    <div className="event-card-sm">
      <div className="event-card-sm__img-wrapper">
        <img src={eventImage} alt={eventTitle} className="event-card-sm__img" />
      </div>
      <div className="event-card-sm__text">
        <h5>{eventTitle}</h5>
        <div className="event-card-sm__chanel">
          <div className="chanel-section">
            <img className="like" src={chanel} alt="" />
            <h4>BBC News</h4>
            <button className="btn-category">Read Now</button>
          </div>
      </div>
      <div className="event-card-sm__ratings">
          <div className="like-section">
            <img className="like" src={like} alt="" />
            <p>44.66</p>
          </div>
          <div className="comment-section">
            <img className="comment" src={comment} alt="" />
            <p>44.55</p>
          </div>
          <button className="save-event">
            <img src={save} alt="Save this event to favorites." />
          </button>
      </div>
      </div>
      
    </div>
  );
};

export default EventCardSM;
