import React, { Component } from "react"
import axios from 'axios'

class Home extends Component{
    constructor(props)
    {
        super(props)
        this.state = {
            posts: []
        }
    }

    componentDidMount(){
      var url =
      "https://newsapi.org/v2/everything?" +
      "q=Apple&" +
      "from=2022-11-27&" +
      "sortBy=popularity&" +
      "apiKey=0740336d1f424297885a13533bd5182e";

    var req = new Request(url);
        axios.get('https://newsapi.org/v2/everything?q=Apple&')
        .then(response => {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }

    render() {
        const { posts } = this.state
        return(
            <div>
                Eveniments List:
                {
                    posts.length?
                    posts.map(post => 
                    <div key = {posts.event_title}> {post.event_title} {post.event_description} </div>):
                    null
                }
            </div>
        )
    }
}

export default Home
