import { getDateFormatted } from "../../helpers/utils";

import save from "../../assets/images/icons/bookmark-light.svg";

const EventCardLG = ({
  eventImage,
  eventDate,
  eventTime,
  eventLocation,
  eventTitle,
  eventDescription,
  peopleJoined,
}) => {
  return (
    <div className="event-card-lg">
      <div className="event-card__info">
        <img className="event-card__img" src={eventImage} alt={eventTitle} />
        <div className="event-card__img-bg"></div>
        <p className="event-card__date">
          <span>{getDateFormatted(eventDate).split(" ")[0]}</span>
          <span>{getDateFormatted(eventDate).split(" ")[1]}</span>
        </p>
        <h5 className="event-card__title">{eventTitle}</h5>
        <p className="event-card__location">{eventLocation}</p>
        <button className="save-btn">
          <img src={save} alt="Save this event to your favorites" />
        </button>
      </div>
      {!!peopleJoined && peopleJoined.length > 0 ? (
        <div className="event-card__joined">
          <p>{`${peopleJoined.length} joined`}</p>
          <div>
            {peopleJoined.map((item, index) => {
              return (
                index < 3 && (
                  <div className="event-card__member" key={index}>
                    <img src={item.userPicture} alt={`Event Guest ${index}`} />
                  </div>
                )
              );
            })}
            <div className="event-card__remaining-members">
              <p>{`+${peopleJoined.length - 3}`}</p>
            </div>
          </div>
        </div>
      ) : (
        <p className="event-card__description">
          {eventDescription.length > 50
            ? eventDescription.slice(0, 52) + "..."
            : eventDescription}
        </p>
      )}
    </div>
  );
};

export default EventCardLG;
